import React, { Component } from 'react';
import EmployeeService from '../services/EmployeeService';

class UpdateEmployeeComponent extends Component {
    constructor(props){
        super(props);
        this.state = {
            firstName: '',
            lastName: '',
            emailId: ''

        }

    }

    componentDidMount(){
        EmployeeService.getEmployeById(this.props.match.params.id).then((res) => {
            let employee = res.data;
            this.setState({
                firstName: employee.firstName,
                lastName: employee.lastName,
                emailId: employee.emailId
            });
        });
    }

    updateEmployee = (e) => {
        e.preventDefault();
        let employee = {firstName: this.state.firstName, lastName: this.state.lastName, emailId: this.state.emailId};

        console.log('employee update => ' + JSON.stringify(employee));

        EmployeeService.updateEmployee(employee, this.state.id)
    }

    changeFirstNameHandler = (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler = (event) => {
        this.setState({lastName: event.target.value});
    }

    changeEmailHandler = (event) =>{
        this.setState({emailId: event.target.value});
    }

    cancel(){
        this.props.history.push('/employees');
    }

    render(){
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3 mt-4">
                            <h3>Update Employee</h3>
                            <form>
                                <div className="form-group">
                                    <label>First Name: </label>
                                    <input placehorder="First Name" name="firstName" className="form-control" value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                                </div>
                                <div className="form-group">
                                    <label className="mt-2">Last Name: </label>
                                    <input placeholder="Last Name" name="lastName" className="form-control" value={this.state.lastName} onChange={this.changeLastNameHandler}/>
                                </div>
                                <div className="form-control">
                                    <label className="mt-2">Email Address: </label>
                                    <input placeholder="Email Address" name="emailId" className="form-control" value={this.state.emailId} onChange={this.changeEmailHandler}/>
                                </div>
                                <button className="btn btn-success mt-3" onClick={this.updateEmployee}>Update</button>
                                <button className="btn btn-danger mt-2" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}

export default UpdateEmployeeComponent;
